// SPDX-License-Identifier: Unlicense
pragma solidity ^0.8.13;

import "./RLPWriter.sol";
import "./Suave.sol";
import "solidity-rlp/contracts/RLPReader.sol";

library Transactions {
    using RLPReader for RLPReader.RLPItem;
    using RLPReader for RLPReader.Iterator;
    using RLPReader for bytes;

    struct EIP155 {
        address to;
        uint256 gas;
        uint256 gasPrice;
        uint256 value;
        uint256 nonce;
        bytes data;
        uint256 chainId;
        bytes32 r;
        bytes32 s;
        uint64 v;
    }

    struct EIP155Request {
        address to;
        uint256 gas;
        uint256 gasPrice;
        uint256 value;
        uint256 nonce;
        bytes data;
        uint256 chainId;
    }

    struct EIP1559 {
        address to;
        uint64 gas;
        uint64 maxFeePerGas;
        uint64 maxPriorityFeePerGas;
        uint64 value;
        uint64 nonce;
        bytes data;
        uint64 chainId;
        bytes accessList;
        bytes32 r;
        bytes32 s;
        uint64 v;
    }

    struct EIP1559Request {
        address to;
        uint64 gas;
        uint64 maxFeePerGas;
        uint64 maxPriorityFeePerGas;
        uint64 value;
        uint64 nonce;
        bytes data;
        uint64 chainId;
        bytes accessList;
    }

    function encodeRLP(EIP155 memory txStruct) internal pure returns (bytes memory) {
        bytes[] memory items = new bytes[](9);

        items[0] = RLPWriter.writeUint(txStruct.nonce);
        items[1] = RLPWriter.writeUint(txStruct.gasPrice);
        items[2] = RLPWriter.writeUint(txStruct.gas);

        if (txStruct.to == address(0)) {
            items[3] = RLPWriter.writeBytes(bytes(""));
        } else {
            items[3] = RLPWriter.writeAddress(txStruct.to);
        }
        items[4] = RLPWriter.writeUint(txStruct.value);
        items[5] = RLPWriter.writeBytes(txStruct.data);
        items[6] = RLPWriter.writeUint(uint256(txStruct.v));
        items[7] = RLPWriter.writeBytes(abi.encodePacked(txStruct.r));
        items[8] = RLPWriter.writeBytes(abi.encodePacked(txStruct.s));

        return RLPWriter.writeList(items);
    }

    function encodeRLP(EIP155Request memory txStruct) internal pure returns (bytes memory) {
        bytes[] memory items = new bytes[](9);

        items[0] = RLPWriter.writeUint(txStruct.nonce);
        items[1] = RLPWriter.writeUint(txStruct.gasPrice);
        items[2] = RLPWriter.writeUint(txStruct.gas);

        if (txStruct.to == address(0)) {
            items[3] = RLPWriter.writeBytes(bytes(""));
        } else {
            items[3] = RLPWriter.writeAddress(txStruct.to);
        }
        items[4] = RLPWriter.writeUint(txStruct.value);
        items[5] = RLPWriter.writeBytes(txStruct.data);
        items[6] = RLPWriter.writeUint(txStruct.chainId);
        items[7] = RLPWriter.writeBytes("");
        items[8] = RLPWriter.writeBytes("");

        return RLPWriter.writeList(items);
    }

    function encodeRLP(EIP1559 memory txStruct) internal pure returns (bytes memory) {
        bytes[] memory items = new bytes[](12);

        items[0] = RLPWriter.writeUint(txStruct.chainId);
        items[1] = RLPWriter.writeUint(txStruct.nonce);
        items[2] = RLPWriter.writeUint(txStruct.maxPriorityFeePerGas);
        items[3] = RLPWriter.writeUint(txStruct.maxFeePerGas);
        items[4] = RLPWriter.writeUint(txStruct.gas);

        if (txStruct.to == address(0)) {
            items[5] = RLPWriter.writeBytes(bytes(""));
        } else {
            items[5] = RLPWriter.writeAddress(txStruct.to);
        }

        items[6] = RLPWriter.writeUint(txStruct.value);
        items[7] = RLPWriter.writeBytes(txStruct.data);

        if (txStruct.accessList.length == 0) {
            items[8] = hex"c0"; // Empty list encoding
        } else {
            items[8] = RLPWriter.writeBytes(txStruct.accessList);
        }

        items[9] = RLPWriter.writeUint(uint256(txStruct.v));
        items[10] = RLPWriter.writeBytes(abi.encodePacked(txStruct.r));
        items[11] = RLPWriter.writeBytes(abi.encodePacked(txStruct.s));

        bytes memory rlpTxn = RLPWriter.writeList(items);

        bytes memory txn = new bytes(1 + rlpTxn.length);
        txn[0] = 0x02;

        for (uint256 i = 0; i < rlpTxn.length; ++i) {
            txn[i + 1] = rlpTxn[i];
        }

        return txn;
    }

    function encodeRLP(EIP1559Request memory txStruct) internal pure returns (bytes memory) {
        bytes[] memory items = new bytes[](9);

        items[0] = RLPWriter.writeUint(txStruct.chainId);
        items[1] = RLPWriter.writeUint(txStruct.nonce);
        items[2] = RLPWriter.writeUint(txStruct.maxPriorityFeePerGas);
        items[3] = RLPWriter.writeUint(txStruct.maxFeePerGas);
        items[4] = RLPWriter.writeUint(txStruct.gas);

        if (txStruct.to == address(0)) {
            items[5] = RLPWriter.writeBytes(bytes(""));
        } else {
            items[5] = RLPWriter.writeAddress(txStruct.to);
        }

        items[6] = RLPWriter.writeUint(txStruct.value);
        items[7] = RLPWriter.writeBytes(txStruct.data);

        if (txStruct.accessList.length == 0) {
            items[8] = hex"c0"; // Empty list encoding
        } else {
            items[8] = RLPWriter.writeBytes(txStruct.accessList);
        }

        bytes memory rlpTxn = RLPWriter.writeList(items);

        bytes memory txn = new bytes(1 + rlpTxn.length);
        txn[0] = 0x02;

        for (uint256 i = 0; i < rlpTxn.length; ++i) {
            txn[i + 1] = rlpTxn[i];
        }

        return txn;
    }

    function decodeRLP_EIP155(bytes memory rlp) internal pure returns (EIP155 memory) {
        EIP155 memory txStruct;

        RLPReader.RLPItem[] memory ls = rlp.toRlpItem().toList();
        require(ls.length == 9, "invalid transaction");

        txStruct.nonce = uint64(ls[0].toUint());
        txStruct.gasPrice = uint64(ls[1].toUint());
        txStruct.gas = uint64(ls[2].toUint());

        if (ls[3].toRlpBytes().length == 1) {
            txStruct.to = address(0);
        } else {
            txStruct.to = ls[3].toAddress();
        }

        txStruct.value = uint64(ls[4].toUint());
        txStruct.data = ls[5].toBytes();
        txStruct.v = uint64(ls[6].toUint());
        txStruct.r = bytesToBytes32(ls[7].toBytes());
        txStruct.s = bytesToBytes32(ls[8].toBytes());

        return txStruct;
    }

    function decodeRLP_EIP155Request(bytes memory rlp) internal pure returns (EIP155Request memory) {
        EIP155Request memory txStruct;

        RLPReader.RLPItem[] memory ls = rlp.toRlpItem().toList();
        require(ls.length == 9, "invalid transaction");

        txStruct.nonce = ls[0].toUint();
        txStruct.gasPrice = ls[1].toUint();
        txStruct.gas = ls[2].toUint();

        if (ls[3].toRlpBytes().length == 1) {
            txStruct.to = address(0);
        } else {
            txStruct.to = ls[3].toAddress();
        }

        txStruct.value = ls[4].toUint();
        txStruct.data = ls[5].toBytes();
        txStruct.chainId = uint64(ls[6].toUint());

        return txStruct;
    }

    function decodeRLP_EIP1559(bytes memory rlp) internal pure returns (EIP1559 memory) {
        EIP1559 memory txStruct;

        bytes memory rlpWithoutPrefix = new bytes(rlp.length - 1);

        for (uint256 i = 0; i < rlp.length - 1; ++i) {
            rlpWithoutPrefix[i] = rlp[i + 1];
        }

        RLPReader.RLPItem[] memory ls = rlpWithoutPrefix.toRlpItem().toList();
        require(ls.length == 12, "invalid transaction");

        txStruct.chainId = uint64(ls[0].toUint());
        txStruct.nonce = uint64(ls[1].toUint());
        txStruct.maxPriorityFeePerGas = uint64(ls[2].toUint());
        txStruct.maxFeePerGas = uint64(ls[3].toUint());
        txStruct.gas = uint64(ls[4].toUint());

        if (ls[5].toRlpBytes().length == 1) {
            txStruct.to = address(0);
        } else {
            txStruct.to = ls[5].toAddress();
        }

        txStruct.value = uint64(ls[6].toUint());
        txStruct.data = ls[7].toBytes();
        txStruct.accessList = ls[8].toBytes();
        txStruct.v = uint64(ls[9].toUint());
        txStruct.r = bytesToBytes32(ls[10].toBytes());
        txStruct.s = bytesToBytes32(ls[11].toBytes());

        return txStruct;
    }

    function decodeRLP_EIP1559Request(bytes memory rlp) internal pure returns (EIP1559Request memory) {
        EIP1559Request memory txStruct;

        bytes memory rlpWithoutPrefix = new bytes(rlp.length - 1);

        for (uint256 i = 0; i < rlp.length - 1; ++i) {
            rlpWithoutPrefix[i] = rlp[i + 1];
        }

        RLPReader.RLPItem[] memory ls = rlpWithoutPrefix.toRlpItem().toList();
        require(ls.length == 8, "invalid transaction");

        txStruct.chainId = uint64(ls[0].toUint());
        txStruct.nonce = uint64(ls[1].toUint());
        txStruct.maxPriorityFeePerGas = uint64(ls[2].toUint());
        txStruct.maxFeePerGas = uint64(ls[3].toUint());
        txStruct.gas = uint64(ls[4].toUint());

        if (ls[5].toRlpBytes().length == 1) {
            txStruct.to = address(0);
        } else {
            txStruct.to = ls[5].toAddress();
        }

        txStruct.value = uint64(ls[6].toUint());
        txStruct.data = ls[7].toBytes();

        return txStruct;
    }

    function bytesToBytes32(bytes memory inBytes) internal pure returns (bytes32 out) {
        require(inBytes.length == 32, "bytesToBytes32: invalid input length");
        assembly {
            out := mload(add(inBytes, 32))
        }
    }

    function signTxn(Transactions.EIP1559Request memory request, string memory signingKey)
        internal
        view
        returns (Transactions.EIP1559 memory response)
    {
        bytes memory rlp = Transactions.encodeRLP(request);
        bytes memory hash = abi.encodePacked(keccak256(rlp));
        bytes memory signature = Suave.signMessage(hash, Suave.CryptoSignature.SECP256, signingKey);
        (uint8 v, bytes32 r, bytes32 s) = decodeSignature(signature);

        response.to = request.to;
        response.gas = request.gas;
        response.maxFeePerGas = request.maxFeePerGas;
        response.maxPriorityFeePerGas = request.maxPriorityFeePerGas;
        response.value = request.value;
        response.nonce = request.nonce;
        response.data = request.data;
        response.chainId = request.chainId;
        response.accessList = request.accessList;
        response.v = v;
        response.r = r;
        response.s = s;

        return response;
    }

    function signTxn(Transactions.EIP155Request memory request, string memory signingKey)
        internal
        view
        returns (Transactions.EIP155 memory response)
    {
        bytes memory rlp = Transactions.encodeRLP(request);
        bytes memory hash = abi.encodePacked(keccak256(rlp));
        bytes memory signature = Suave.signMessage(hash, Suave.CryptoSignature.SECP256, signingKey);

        // TODO: check overflow
        uint64 chainIdMul = uint64(request.chainId) * 2;
        (uint8 v, bytes32 r, bytes32 s) = decodeSignature(signature);

        uint64 v64 = uint64(v) + 35;
        v64 += chainIdMul;

        response.to = request.to;
        response.gas = request.gas;
        response.gasPrice = request.gasPrice;
        response.value = request.value;
        response.nonce = request.nonce;
        response.data = request.data;
        response.chainId = request.chainId;
        response.v = v64;
        response.r = r;
        response.s = s;

        return response;
    }

    function decodeSignature(bytes memory signature) public pure returns (uint8 v, bytes32 r, bytes32 s) {
        assembly {
            r := mload(add(signature, 0x20))
            s := mload(add(signature, 0x40))
            v := byte(0, mload(add(signature, 0x60)))
        }
    }
}

contracts/oracle/BinanceOracle.sol.content:
// SPDX-License-Identifier: MIT
// Author: Miha Lotric (halo3mic)

pragma solidity ^0.8.13;

import { AnyBundleContract, Suave } from "../standard_peekers/bids.sol";
import { SuaveContract } from "../blockad/lib/SuaveContract.sol";
import "solady/src/utils/JSONParserLib.sol";
import "solady/src/utils/LibString.sol";
import "../libraries/Transactions.sol";
import "../libraries/Bundle.sol";


contract BinanceOracle is SuaveContract {
    using JSONParserLib for *;

    uint public constant HOLESKY_CHAINID = 17000;
    string public constant HOLESKY_CHAINID_STR = "0x4268";
    uint8 public constant DECIMALS = 4;
    string public constant S_NAMESPACE = "oracle:v0:pksecret";
    string public constant REMOTE_HOLESKY_RPC = "https://ethereum-holesky-rpc.publicnode.com";
    string public constant URL_PARTIAL = "https://data-api.binance.vision/api/v3/ticker/price?symbol=";
    string public constant HOLESKY_BUNDLE_ENDPOINT = "https://relay-holesky.flashbots.net";

    bool public isInitialized;
    Suave.DataId public pkBidId;
    address public controller;
    address public settlementContract;

    event PriceSubmission(string ticker, uint price);

    // ⛓️ EVM Methods

    function confidentialConstructorCallback(
        Suave.DataId _pkBidId, 
        address pkAddress
    ) public {
        crequire(!isInitialized, "Already initialized");
        pkBidId = _pkBidId;
        controller = pkAddress;
        isInitialized = true;
    }

    function registerCallback(address _settlementContract) public {
        require(settlementContract == address(0), "Already registered");
        settlementContract = _settlementContract;
    }

    // ! Warning: This method is not restricted and emitted events should not be relied upon
    function queryAndSubmitCallback(string memory ticker, uint price) public {
        emit PriceSubmission(ticker, price);
    }

    fallback() external payable {
        // Needed to accept MEVM calls with no callbacks
    }

    // 🤐 MEVM Methods

    function confidentialConstructor() external view onlyConfidential returns (bytes memory) {
        crequire(!isInitialized, "Already initialized");

        string memory pk = Suave.privateKeyGen(Suave.CryptoSignature.SECP256);
        address pkAddress = getAddressForPk(pk);
		Suave.DataId bidId = storePK(bytes(pk));

        return abi.encodeWithSelector(
            this.confidentialConstructorCallback.selector, 
            bidId, 
            pkAddress
        );
    }

    function registerSettlementContract(
        address _settlementContract
    ) external view onlyConfidential() returns (bytes memory) {
        // Allow multiple registrations for the same address (consider the intial tx is not commited to the chain)
        require(_settlementContract == settlementContract || settlementContract == address(0), "Already registered");
        bytes memory signedTx = createRegisterTx(_settlementContract);
        sendRawTx(signedTx);
        return abi.encodeWithSelector(this.registerCallback.selector, _settlementContract);
    }

    function queryAndSubmit(
        string memory ticker,
        uint nonce,
        uint gasPrice,
        uint64 settlementBlockNum,
        bool privateSubmission
    ) external view onlyConfidential returns (bytes memory) {
        uint price = queryLatestPrice(ticker);
        submitPriceUpdate(ticker, price, nonce, gasPrice, settlementBlockNum, privateSubmission);
        return abi.encodeWithSelector(this.queryAndSubmitCallback.selector, ticker, price);
    }

    function queryLatestPrice(string memory ticker) public view returns (uint price) {
        bytes memory response = doBinanceQuery(ticker);
        JSONParserLib.Item memory parsedRes = string(response).parse();
        // solhint-disable-next-line
        string memory priceStr = string(parsedRes.at('"price"').value());
        price = floatToInt(trimStrEdges(priceStr), DECIMALS);
    }

    function submitPriceUpdate(
        string memory ticker,
        uint price, 
        uint nonce,
        uint gasPrice,
        uint64 settlementBlockNum,
        bool privateSubmission
    ) internal view {
        bytes memory signedTx = createPriceUpdateTx(ticker, price, nonce, gasPrice);
        if (privateSubmission) {
            sendBundle(signedTx, settlementBlockNum);
        } else {
            sendRawTx(signedTx);
        }
    }

    function createRegisterTx(address _settlementContract) internal view returns (bytes memory txSigned) {
        Transactions.EIP155 memory transaction = Transactions.EIP155({
            nonce: 0,
            gasPrice: 100 gwei,
            gas: 100_000,
            to: _settlementContract,
            value: 0,
            data: abi.encodeWithSignature("register()"),
            chainId: HOLESKY_CHAINID,
            v: 27,
            r: hex"1111111111111111111111111111111111111111111111111111111111111111",
            s: hex"1111111111111111111111111111111111111111111111111111111111111111"
        });
        bytes memory txRlp = Transactions.encodeRLP(transaction);
        string memory pk = retreivePK();
        txSigned = Suave.signEthTransaction(txRlp, HOLESKY_CHAINID_STR, pk);
    }

    function createPriceUpdateTx(
        string memory ticker, 
        uint price, 
        uint nonce, 
        uint gasPrice
    ) internal view returns (bytes memory txSigned)  {
        Transactions.EIP155 memory transaction = Transactions.EIP155({
            nonce: nonce,
            gasPrice: gasPrice,
            gas: 100_000,
            to: settlementContract,
            value: 0,
            data: abi.encodeWithSignature("updatePrice(string,uint256)", ticker, price),
            chainId: HOLESKY_CHAINID,
            v: 27,
            r: hex"1111111111111111111111111111111111111111111111111111111111111111",
            s: hex"1111111111111111111111111111111111111111111111111111111111111111"
        });
        bytes memory txRlp = Transactions.encodeRLP(transaction);
        string memory pk = retreivePK();
        txSigned = Suave.signEthTransaction(txRlp, HOLESKY_CHAINID_STR, pk);
    }

    function sendRawTx(bytes memory txSigned) public view returns (bytes memory) {
        /* solhint-disable */
        bytes memory body = abi.encodePacked(
            '{"jsonrpc":"2.0","method":"eth_sendRawTransaction","params":["', 
            LibString.toHexString(txSigned), 
            '"],"id":1}'
        );
        /* solhint-enable */
        Suave.HttpRequest memory request;
        request.method = "POST";
        request.body = body;
        request.headers = new string[](1);
        request.headers[0] = "Content-Type: application/json";
        request.withFlashbotsSignature = false;
        request.url = REMOTE_HOLESKY_RPC;
        return doHttpRequest(request);
    }

    function sendBundle(bytes memory txSigned, uint64 settlementBlockNum) internal view {
        simulateTx(txSigned);
        sendTxViaBundle(txSigned, settlementBlockNum);
    }

    function simulateTx(bytes memory signedTx) internal view {
        // solhint-disable-next-line
        bytes memory bundle = abi.encodePacked('{"txs": ["', LibString.toHexString(signedTx), '"]}');
        (bool successSim, bytes memory data) = Suave.SIMULATE_BUNDLE.staticcall(abi.encode(bundle));
        crequire(successSim,  string(abi.encodePacked("BundleSimulationFailed: ", string(data))));
    }

    function doBinanceQuery(string memory ticker) internal view returns (bytes memory) {
        string[] memory headers = new string[](1);
        headers[0] = "Content-Type: application/json";
        Suave.HttpRequest memory request = Suave.HttpRequest({
            url: string(abi.encodePacked(URL_PARTIAL, ticker)),
            method: "GET",
            headers: headers,
            body: new bytes(0),
            withFlashbotsSignature: false
        });
        return doHttpRequest(request);
    }

    function doHttpRequest(Suave.HttpRequest memory request) internal view returns (bytes memory) {
        (bool success, bytes memory data) = Suave.DO_HTTPREQUEST.staticcall(abi.encode(request));
        crequire(success, string(data));
        return abi.decode(data, (bytes));
    }

    function sendTxViaBundle(bytes memory txSigned, uint64 settlementBlockNum) internal view {
        bytes[] memory txns = new bytes[](1);
        txns[0] = txSigned;
        bytes memory bundleReqParams = bundleRequestParams(txns, settlementBlockNum);
        (bool successReq, bytes memory dataReq) = Suave.SUBMIT_BUNDLE_JSON_RPC.staticcall(abi.encode(
            HOLESKY_BUNDLE_ENDPOINT, 
            "eth_sendBundle", 
            bundleReqParams
        ));
        crequire(successReq, string(abi.encodePacked("BundleSubmissionFailed: ", string(dataReq))));
    }

    function bundleRequestParams(bytes[] memory txns, uint blockNumber) internal pure returns (bytes memory) {
        // solhint-disable-next-line
        bytes memory params = abi.encodePacked('{"blockNumber": "', LibString.toHexString(blockNumber), '", "txs": [');
        for (uint256 i = 0; i < txns.length; i++) {
            // solhint-disable-next-line
            params = abi.encodePacked(params, '"', LibString.toHexString(txns[i]), '"');
            if (i < txns.length - 1) {
                params = abi.encodePacked(params, ",");
            } else {
                params = abi.encodePacked(params, "]");
            }
        }
        params = abi.encodePacked(params, "}");

        return params;
    }

    function storePK(bytes memory pk) internal view returns (Suave.DataId) {
		address[] memory peekers = new address[](3);
		peekers[0] = address(this);
		peekers[1] = Suave.FETCH_DATA_RECORDS;
		peekers[2] = Suave.CONFIDENTIAL_RETRIEVE;
		Suave.DataRecord memory secretBid = Suave.newDataRecord(0, peekers, peekers, S_NAMESPACE);
		Suave.confidentialStore(secretBid.id, S_NAMESPACE, pk);
		return secretBid.id;
	}

    function retreivePK() internal view returns (string memory) {
        bytes memory pkBytes =  Suave.confidentialRetrieve(pkBidId, S_NAMESPACE);
        return string(pkBytes);
    }

}

// 🔧 Utils

function floatToInt(string memory floatString, uint8 decimals) pure returns (uint) {
    bytes memory stringBytes = bytes(floatString);
    uint dotPosition;

    // Find the position of the dot
    for (uint i = 0; i < stringBytes.length; i++) {
        if (stringBytes[i] == 0x2E) {
            dotPosition = i;
            break;
        }
    }

    uint integerPart = 0;
    uint decimalPart = 0;
    uint tenPower = 1;

    // Convert integer part
    for (uint i = dotPosition; i > 0; i--) {
        integerPart += (uint8(stringBytes[i - 1]) - 48) * tenPower;
        tenPower *= 10;
    }
    // Reset power of ten
    tenPower = 1;
    // Convert decimal part
    for (uint i = dotPosition+decimals; i > dotPosition; i--) {
        decimalPart += (uint8(stringBytes[i]) - 48) * tenPower;
        tenPower *= 10;
    }
    // Combine integer and decimal parts
    return integerPart * (10**decimals) + decimalPart;
}

function trimStrEdges(string memory _input) pure returns (string memory) {
    bytes memory input = bytes(_input);
    require(input.length > 2, "Input too short");

    uint newLength = input.length - 2;
    bytes memory result = new bytes(newLength);

    assembly {
        let inputPtr := add(input, 0x21)
        let resultPtr := add(result, 0x20)
        let length := mload(input)
        mstore(resultPtr, mload(inputPtr))
        mstore(result, newLength)
    }
    return string(result);
}

function getAddressForPk(string memory pk) view returns (address) {
    bytes32 digest = keccak256(abi.encode("yo"));
    bytes memory sig = Suave.signMessage(abi.encodePacked(digest), Suave.CryptoSignature.SECP256, pk);
    return recoverSigner(digest, sig);
}

function recoverSigner(bytes32 _ethSignedMessageHash, bytes memory _signature) pure returns (address) {
    (bytes32 r, bytes32 s, uint8 v) = splitSignature(_signature);
    return ecrecover(_ethSignedMessageHash, v, r, s);
}

function splitSignature(bytes memory sig) pure returns (bytes32 r, bytes32 s, uint8 v) {
    require(sig.length == 65, "invalid signature length");
    assembly {
        r := mload(add(sig, 32))
        s := mload(add(sig, 64))
        v := byte(0, mload(add(sig, 96)))
    }
    if (v < 27) {
        v += 27;
    }
}

contracts/standard_peekers/bids.sol.content:
pragma solidity ^0.8.8;

import "../libraries/Suave.sol";

contract AnyBundleContract {
    event DataRecordEvent(Suave.DataId dataId, uint64 decryptionCondition, address[] allowedPeekers);

    function fetchConfidentialBundleData() public returns (bytes memory) {
        require(Suave.isConfidential());

        bytes memory confidentialInputs = Suave.confidentialInputs();
        return abi.decode(confidentialInputs, (bytes));
    }

    function emitDataRecord(Suave.DataRecord calldata dataRecord) public {
        emit DataRecordEvent(dataRecord.id, dataRecord.decryptionCondition, dataRecord.allowedPeekers);
    }
}

contract BundleContract is AnyBundleContract {
    function newBundle(
        uint64 decryptionCondition,
        address[] memory dataAllowedPeekers,
        address[] memory dataAllowedStores
    ) external payable returns (bytes memory) {
        require(Suave.isConfidential());

        bytes memory bundleData = this.fetchConfidentialBundleData();

        uint64 egp = Suave.simulateBundle(bundleData);

        Suave.DataRecord memory dataRecord =
            Suave.newDataRecord(decryptionCondition, dataAllowedPeekers, dataAllowedStores, "default:v0:ethBundles");

        Suave.confidentialStore(dataRecord.id, "default:v0:ethBundles", bundleData);
        Suave.confidentialStore(dataRecord.id, "default:v0:ethBundleSimResults", abi.encode(egp));

        return emitAndReturn(dataRecord, bundleData);
    }

    function emitAndReturn(Suave.DataRecord memory dataRecord, bytes memory) internal virtual returns (bytes memory) {
        emit DataRecordEvent(dataRecord.id, dataRecord.decryptionCondition, dataRecord.allowedPeekers);
        return bytes.concat(this.emitDataRecord.selector, abi.encode(dataRecord));
    }
}

contract EthBundleSenderContract is BundleContract {
    string[] public builderUrls;

    constructor(string[] memory builderUrls_) {
        builderUrls = builderUrls_;
    }

    function emitAndReturn(Suave.DataRecord memory dataRecord, bytes memory bundleData)
        internal
        virtual
        override
        returns (bytes memory)
    {
        for (uint256 i = 0; i < builderUrls.length; i++) {
            Suave.submitBundleJsonRPC(builderUrls[i], "eth_sendBundle", bundleData);
        }

        return BundleContract.emitAndReturn(dataRecord, bundleData);
    }
}

contract MevShareContract is AnyBundleContract {
    event HintEvent(Suave.DataId dataId, bytes hint);

    event MatchEvent(Suave.DataId matchDataId, bytes matchHint);

    function newTransaction(
        uint64 decryptionCondition,
        address[] memory dataAllowedPeekers,
        address[] memory dataAllowedStores
    ) external payable returns (bytes memory) {
        // 0. check confidential execution
        require(Suave.isConfidential());

        // 1. fetch bundle data
        bytes memory bundleData = this.fetchConfidentialBundleData();

        // 2. sim bundle
        uint64 egp = Suave.simulateBundle(bundleData);

        // 3. extract hint
        bytes memory hint = Suave.extractHint(bundleData);

        // // 4. store bundle and sim results
        Suave.DataRecord memory dataRecord = Suave.newDataRecord(
            decryptionCondition, dataAllowedPeekers, dataAllowedStores, "mevshare:v0:unmatchedBundles"
        );
        Suave.confidentialStore(dataRecord.id, "mevshare:v0:ethBundles", bundleData);
        Suave.confidentialStore(dataRecord.id, "mevshare:v0:ethBundleSimResults", abi.encode(egp));
        emit DataRecordEvent(dataRecord.id, dataRecord.decryptionCondition, dataRecord.allowedPeekers);
        emit HintEvent(dataRecord.id, hint);

        // // 5. return "callback" to emit hint onchain
        return bytes.concat(this.emitDataRecordAndHint.selector, abi.encode(dataRecord, hint));
    }

    function emitDataRecordAndHint(Suave.DataRecord calldata dataRecord, bytes memory hint) public {
        emit DataRecordEvent(dataRecord.id, dataRecord.decryptionCondition, dataRecord.allowedPeekers);
        emit HintEvent(dataRecord.id, hint);
    }

    function newMatch(
        uint64 decryptionCondition,
        address[] memory dataAllowedPeekers,
        address[] memory dataAllowedStores,
        Suave.DataId sharedataId
    ) external payable returns (bytes memory) {
        // WARNING : this function will copy the original mev share bid
        // into a new key with potentially different permsissions

        require(Suave.isConfidential());
        // 1. fetch confidential data
        bytes memory matchBundleData = this.fetchConfidentialBundleData();

        // 2. sim match alone for validity
        uint64 egp = Suave.simulateBundle(matchBundleData);

        // 3. extract hint
        bytes memory matchHint = Suave.extractHint(matchBundleData);

        Suave.DataRecord memory dataRecord = Suave.newDataRecord(
            decryptionCondition, dataAllowedPeekers, dataAllowedStores, "mevshare:v0:matchDataRecords"
        );
        Suave.confidentialStore(dataRecord.id, "mevshare:v0:ethBundles", matchBundleData);
        Suave.confidentialStore(dataRecord.id, "mevshare:v0:ethBundleSimResults", abi.encode(0));

        //4. merge data records
        Suave.DataId[] memory dataRecords = new Suave.DataId[](2);
        dataRecords[0] = sharedataId;
        dataRecords[1] = dataRecord.id;
        Suave.confidentialStore(dataRecord.id, "mevshare:v0:mergedDataRecords", abi.encode(dataRecords));

        return emitMatchDataRecordAndHint(dataRecord, matchHint);
    }

    function emitMatchDataRecordAndHint(Suave.DataRecord memory dataRecord, bytes memory matchHint)
        internal
        virtual
        returns (bytes memory)
    {
        emit DataRecordEvent(dataRecord.id, dataRecord.decryptionCondition, dataRecord.allowedPeekers);
        emit MatchEvent(dataRecord.id, matchHint);

        return bytes.concat(this.emitDataRecord.selector, abi.encode(dataRecord));
    }
}

contract MevShareBundleSenderContract is MevShareContract {
    string[] public builderUrls;

    constructor(string[] memory builderUrls_) {
        builderUrls = builderUrls_;
    }

    function emitMatchDataRecordAndHint(Suave.DataRecord memory dataRecord, bytes memory matchHint)
        internal
        virtual
        override
        returns (bytes memory)
    {
        bytes memory bundleData = Suave.fillMevShareBundle(dataRecord.id);
        for (uint256 i = 0; i < builderUrls.length; i++) {
            Suave.submitBundleJsonRPC(builderUrls[i], "mev_sendBundle", bundleData);
        }

        return MevShareContract.emitMatchDataRecordAndHint(dataRecord, matchHint);
    }
}

/* Not tested or implemented on the precompile side */
struct EgpRecordPair {
    uint64 egp; // in wei, beware overflow
    Suave.DataId dataId;
}

contract EthBlockContract is AnyBundleContract {
    event BuilderBoostBidEvent(Suave.DataId dataId, bytes builderBid);

    function idsEqual(Suave.DataId _l, Suave.DataId _r) public pure returns (bool) {
        bytes memory l = abi.encodePacked(_l);
        bytes memory r = abi.encodePacked(_r);
        for (uint256 i = 0; i < l.length; i++) {
            if (bytes(l)[i] != r[i]) {
                return false;
            }
        }

        return true;
    }

    function buildMevShare(Suave.BuildBlockArgs memory blockArgs, uint64 blockHeight) public returns (bytes memory) {
        require(Suave.isConfidential());

        Suave.DataRecord[] memory allShareMatchDataRecords =
            Suave.fetchDataRecords(blockHeight, "mevshare:v0:matchDataRecords");
        Suave.DataRecord[] memory allShareUserDataRecords =
            Suave.fetchDataRecords(blockHeight, "mevshare:v0:unmatchedBundles");

        if (allShareUserDataRecords.length == 0) {
            revert Suave.PeekerReverted(address(this), "no data records");
        }

        Suave.DataRecord[] memory allRecords = new Suave.DataRecord[](allShareUserDataRecords.length);
        for (uint256 i = 0; i < allShareUserDataRecords.length; i++) {
            // TODO: sort matches by egp first!
            Suave.DataRecord memory dataRecordToInsert = allShareUserDataRecords[i]; // will be updated with the best match if any
            for (uint256 j = 0; j < allShareMatchDataRecords.length; j++) {
                // TODO: should be done once at the start and sorted
                Suave.DataId[] memory mergeddataIds = abi.decode(
                    Suave.confidentialRetrieve(allShareMatchDataRecords[j].id, "mevshare:v0:mergedDataRecords"),
                    (Suave.DataId[])
                );
                if (idsEqual(mergeddataIds[0], allShareUserDataRecords[i].id)) {
                    dataRecordToInsert = allShareMatchDataRecords[j];
                    break;
                }
            }
            allRecords[i] = dataRecordToInsert;
        }

        EgpRecordPair[] memory bidsByEGP = new EgpRecordPair[](allRecords.length);
        for (uint256 i = 0; i < allRecords.length; i++) {
            bytes memory simResults = Suave.confidentialRetrieve(allRecords[i].id, "mevshare:v0:ethBundleSimResults");
            uint64 egp = abi.decode(simResults, (uint64));
            bidsByEGP[i] = EgpRecordPair(egp, allRecords[i].id);
        }

        // Bubble sort, cause why not
        uint256 n = bidsByEGP.length;
        for (uint256 i = 0; i < n - 1; i++) {
            for (uint256 j = i + 1; j < n; j++) {
                if (bidsByEGP[i].egp < bidsByEGP[j].egp) {
                    EgpRecordPair memory temp = bidsByEGP[i];
                    bidsByEGP[i] = bidsByEGP[j];
                    bidsByEGP[j] = temp;
                }
            }
        }

        Suave.DataId[] memory alldataIds = new Suave.DataId[](allRecords.length);
        for (uint256 i = 0; i < bidsByEGP.length; i++) {
            alldataIds[i] = bidsByEGP[i].dataId;
        }

        return buildAndEmit(blockArgs, blockHeight, alldataIds, "mevshare:v0");
    }

    function buildFromPool(Suave.BuildBlockArgs memory blockArgs, uint64 blockHeight) public returns (bytes memory) {
        require(Suave.isConfidential());

        Suave.DataRecord[] memory allRecords = Suave.fetchDataRecords(blockHeight, "default:v0:ethBundles");
        if (allRecords.length == 0) {
            revert Suave.PeekerReverted(address(this), "no data records");
        }

        EgpRecordPair[] memory bidsByEGP = new EgpRecordPair[](allRecords.length);
        for (uint256 i = 0; i < allRecords.length; i++) {
            bytes memory simResults = Suave.confidentialRetrieve(allRecords[i].id, "default:v0:ethBundleSimResults");
            uint64 egp = abi.decode(simResults, (uint64));
            bidsByEGP[i] = EgpRecordPair(egp, allRecords[i].id);
        }

        // Bubble sort, cause why not
        uint256 n = bidsByEGP.length;
        for (uint256 i = 0; i < n - 1; i++) {
            for (uint256 j = i + 1; j < n; j++) {
                if (bidsByEGP[i].egp < bidsByEGP[j].egp) {
                    EgpRecordPair memory temp = bidsByEGP[i];
                    bidsByEGP[i] = bidsByEGP[j];
                    bidsByEGP[j] = temp;
                }
            }
        }

        Suave.DataId[] memory alldataIds = new Suave.DataId[](allRecords.length);
        for (uint256 i = 0; i < bidsByEGP.length; i++) {
            alldataIds[i] = bidsByEGP[i].dataId;
        }

        return buildAndEmit(blockArgs, blockHeight, alldataIds, "");
    }

    function buildAndEmit(
        Suave.BuildBlockArgs memory blockArgs,
        uint64 blockHeight,
        Suave.DataId[] memory records,
        string memory relayUrl
    ) public virtual returns (bytes memory) {
        require(Suave.isConfidential());

        (Suave.DataRecord memory blockBid, bytes memory builderBid) =
            this.doBuild(blockArgs, blockHeight, records, relayUrl);

        emit BuilderBoostBidEvent(blockBid.id, builderBid);
        emit DataRecordEvent(blockBid.id, blockBid.decryptionCondition, blockBid.allowedPeekers);
        return bytes.concat(this.emitBuilderBidAndBid.selector, abi.encode(blockBid, builderBid));
    }

    function doBuild(
        Suave.BuildBlockArgs memory blockArgs,
        uint64 blockHeight,
        Suave.DataId[] memory records,
        string memory relayUrl
    ) public view returns (Suave.DataRecord memory, bytes memory) {
        address[] memory allowedPeekers = new address[](2);
        allowedPeekers[0] = address(this);
        allowedPeekers[1] = Suave.BUILD_ETH_BLOCK;

        Suave.DataRecord memory blockBid =
            Suave.newDataRecord(blockHeight, allowedPeekers, allowedPeekers, "default:v0:mergedDataRecords");
        Suave.confidentialStore(blockBid.id, "default:v0:mergedDataRecords", abi.encode(records));

        // todo: specify the builder - mainnet/holesky
        (bytes memory builderBid, bytes memory payload) = Suave.buildEthBlock(blockArgs, blockBid.id, relayUrl);
        Suave.confidentialStore(blockBid.id, "default:v0:builderPayload", payload); // only through this.unlock

        return (blockBid, builderBid);
    }

    function emitBuilderBidAndBid(Suave.DataRecord memory dataRecord, bytes memory builderBid)
        public
        returns (Suave.DataRecord memory, bytes memory)
    {
        emit BuilderBoostBidEvent(dataRecord.id, builderBid);
        emit DataRecordEvent(dataRecord.id, dataRecord.decryptionCondition, dataRecord.allowedPeekers);
        return (dataRecord, builderBid);
    }

    function unlock(Suave.DataId dataId, bytes memory signedBlindedHeader) public view returns (bytes memory) {
        require(Suave.isConfidential());

        // TODO: verify the header is correct
        // TODO: incorporate protocol name
        bytes memory payload = Suave.confidentialRetrieve(dataId, "default:v0:builderPayload");
        return payload;
    }
}

contract EthBlockBidSenderContract is EthBlockContract {
    string boostRelayUrl;

    constructor(string memory boostRelayUrl_) {
        boostRelayUrl = boostRelayUrl_;
    }

    function buildAndEmit(
        Suave.BuildBlockArgs memory blockArgs,
        uint64 blockHeight,
        Suave.DataId[] memory dataRecords,
        string memory namespace
    ) public virtual override returns (bytes memory) {
        require(Suave.isConfidential());

        (Suave.DataRecord memory blockDataRecord, bytes memory builderBid) =
            this.doBuild(blockArgs, blockHeight, dataRecords, namespace);
        Suave.submitEthBlockToRelay(boostRelayUrl, builderBid);

        emit DataRecordEvent(blockDataRecord.id, blockDataRecord.decryptionCondition, blockDataRecord.allowedPeekers);
        return bytes.concat(this.emitDataRecord.selector, abi.encode(blockDataRecord));
    }
}

